glsl4.vert:
precision mediump float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
layout(set = 2, binding = 0) uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
  highp vec4 cc_lightingMapUVParam;
};
float LinearFog(vec4 pos) {
  vec4 wPos = pos;
  float cam_dis = distance(cc_cameraPos, wPos);
  float fogStart = cc_fogBase.x;
  float fogEnd = cc_fogBase.y;
  return clamp((fogEnd - cam_dis) / (fogEnd - fogStart), 0., 1.);
}
float ExpFog(vec4 pos) {
  vec4 wPos = pos;
  float fogAtten = cc_fogAdd.z;
  float fogStart = cc_fogBase.x;
  float fogDensity = cc_fogBase.z;
  float cam_dis = max(distance(cc_cameraPos, wPos) - fogStart, 0.0) / fogAtten * 4.;
  float f = exp(-cam_dis * fogDensity);
  return f;
}
float ExpSquaredFog(vec4 pos) {
  vec4 wPos = pos;
  float fogAtten = cc_fogAdd.z;
  float fogStart = cc_fogBase.x;
  float fogDensity = cc_fogBase.z;
  float cam_dis = max(distance(cc_cameraPos, wPos) - fogStart, 0.0) / fogAtten * 4.;
  float f = exp(-cam_dis * cam_dis * fogDensity * fogDensity);
  return f;
}
float LayeredFog(vec4 pos) {
  vec4 wPos = pos;
  float fogAtten = cc_fogAdd.z;
  float _FogTop = cc_fogAdd.x;
  float _FogRange = cc_fogAdd.y;
  vec3 camWorldProj = cc_cameraPos.xyz;
  camWorldProj.y = 0.;
  vec3 worldPosProj = wPos.xyz;
  worldPosProj.y = 0.;
  float fDeltaD = distance(worldPosProj, camWorldProj) / fogAtten * 2.0;
  float fDeltaY, fDensityIntegral;
  if (cc_cameraPos.y > _FogTop) {
    if (wPos.y < _FogTop) {
      fDeltaY = (_FogTop - wPos.y) / _FogRange * 2.0;
      fDensityIntegral = fDeltaY * fDeltaY * 0.5;
    } else {
      fDeltaY = 0.;
      fDensityIntegral = 0.;
    }
  } else {
    if (wPos.y < _FogTop) {
      float fDeltaA = (_FogTop - cc_cameraPos.y) / _FogRange * 2.;
      float fDeltaB = (_FogTop - wPos.y) / _FogRange * 2.;
      fDeltaY = abs(fDeltaA - fDeltaB);
      fDensityIntegral = abs((fDeltaA * fDeltaA * 0.5) - (fDeltaB * fDeltaB * 0.5));
    } else {
      fDeltaY = abs(_FogTop - cc_cameraPos.y) / _FogRange * 2.;
      fDensityIntegral = abs(fDeltaY * fDeltaY * 0.5);
    }
  }
  float fDensity;
  if (fDeltaY != 0.) {
    fDensity = (sqrt(1.0 + ((fDeltaD / fDeltaY) * (fDeltaD / fDeltaY)))) * fDensityIntegral;
  } else {
    fDensity = 0.;
  }
  float f = exp(-fDensity);
  return f;
}
void CC_TRANSFER_FOG_BASE(vec4 pos, out float factor)
{
  #if CC_USE_FOG == 0
	factor = LinearFog(pos);
  #elif CC_USE_FOG == 1
    factor = ExpFog(pos);
  #elif CC_USE_FOG == 2
    factor = ExpSquaredFog(pos);
  #elif CC_USE_FOG == 3
    factor = LayeredFog(pos);
  #else
    factor = 1.0;
  #endif
}
#if !CC_USE_ACCURATE_FOG
layout(location = 0) out float v_fog_factor;
#endif
void CC_TRANSFER_FOG(vec4 pos) {
#if !CC_USE_ACCURATE_FOG
    CC_TRANSFER_FOG_BASE(pos, v_fog_factor);
#endif
}
layout(location = 1) out highp vec4 v_shadowPos;
layout(set = 0, binding = 2) uniform CCShadow {
  highp mat4 cc_matLightPlaneProj;
  highp mat4 cc_matLightView;
  highp mat4 cc_matLightViewProj;
  highp vec4 cc_shadowInvProjDepthInfo;
  highp vec4 cc_shadowProjDepthInfo;
  highp vec4 cc_shadowProjInfo;
  lowp  vec4 cc_shadowNFLSInfo;
  lowp  vec4 cc_shadowWHPBInfo;
  lowp  vec4 cc_shadowLPNNInfo;
  lowp  vec4 cc_shadowColor;
};
#if CC_RECEIVE_SHADOW
  layout(set = 0, binding = 3) uniform highp sampler2D cc_shadowMap;
  layout(set = 0, binding = 5) uniform highp sampler2D cc_spotLightingMap;
#endif
layout(location = 0) in vec3 a_position;
layout(location = 1) in vec3 a_normal;
layout(location = 2) in vec2 a_texCoord;
layout(location = 2) out highp vec3 v_position;
layout(location = 3) out mediump vec3 v_normal;
#if USE_NORMALMAP
  layout(location = 4) out mediump vec3 v_tangent;
  layout(location = 5) out mediump vec3 v_binormal;
#endif
layout(location = 6) out mediump vec2 uvw;
layout(location = 7) out mediump vec2 uv0;
layout(location = 8) out mediump vec2 uv1;
layout(location = 9) out mediump vec2 uv2;
layout(location = 10) out mediump vec2 uv3;
layout(location = 11) out mediump vec3 luv;
layout(location = 12) out mediump vec3 diffuse;
layout(set = 1, binding = 0) uniform TexCoords {
  vec4 UVScale;
  vec4 lightMapUVParam;
};
void main () {
  vec3 worldPos;
  worldPos.x = cc_matWorld[3][0] + a_position.x;
  worldPos.y = cc_matWorld[3][1] + a_position.y;
  worldPos.z = cc_matWorld[3][2] + a_position.z;
  vec4 pos = vec4(worldPos, 1.0);
  pos = cc_matViewProj * pos;
  uvw = a_texCoord;
  uv0 = a_position.xz * UVScale.x;
  uv1 = a_position.xz * UVScale.y;
  uv2 = a_position.xz * UVScale.z;
  uv3 = a_position.xz * UVScale.w;
  #if USE_LIGHTMAP
    luv.xy = lightMapUVParam.xy + a_texCoord * lightMapUVParam.zw;
    luv.z = lightMapUVParam.z;
  #endif
  v_position = worldPos;
  v_normal = a_normal;
  CC_TRANSFER_FOG(vec4(worldPos, 1.0));
  #if USE_NORMALMAP
    v_tangent = vec3(1.0, 0.0, 0.0);
    v_binormal = vec3(0.0, 0.0, 1.0);
    v_binormal = cross(v_tangent, a_normal);
    v_tangent = cross(a_normal, v_binormal);
  #endif
  v_shadowPos = cc_matLightViewProj * vec4(worldPos, 1.0);
  gl_Position = pos;
}

glsl4.frag:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
vec3 SRGBToLinear (vec3 gamma) {
  return gamma * gamma;
}
layout(set = 0, binding = 2) uniform CCShadow {
  highp mat4 cc_matLightPlaneProj;
  highp mat4 cc_matLightView;
  highp mat4 cc_matLightViewProj;
  highp vec4 cc_shadowInvProjDepthInfo;
  highp vec4 cc_shadowProjDepthInfo;
  highp vec4 cc_shadowProjInfo;
  lowp  vec4 cc_shadowNFLSInfo;
  lowp  vec4 cc_shadowWHPBInfo;
  lowp  vec4 cc_shadowLPNNInfo;
  lowp  vec4 cc_shadowColor;
};
float CCGetLinearDepthFromViewSpace(vec3 viewPos) {
  float dist = length(viewPos);
  return (dist - cc_shadowNFLSInfo.x) / (cc_shadowNFLSInfo.y - cc_shadowNFLSInfo.x);
}
float CCGetLinearDepth(vec3 worldPos) {
  vec4 viewStartPos = cc_matLightView * vec4(worldPos.xyz, 1.0);
  return CCGetLinearDepthFromViewSpace(viewStartPos.xyz);
}
#if CC_RECEIVE_SHADOW
  layout(set = 0, binding = 3) uniform highp sampler2D cc_shadowMap;
  layout(set = 0, binding = 5) uniform highp sampler2D cc_spotLightingMap;
  vec4 ApplyShadowDepthBias_FaceNormal(vec4 shadowPos, vec3 worldNormal)
  {
    vec4 newShadowPos = shadowPos;
    if(cc_shadowLPNNInfo.z > 0.0001)
    {
      vec4 viewNormal = cc_matLightView * vec4(worldNormal, 0.0);
      if(viewNormal.z < 0.1)
        newShadowPos.xy += viewNormal.xy * cc_shadowProjInfo.xy * cc_shadowLPNNInfo.z * clamp(viewNormal.z, 0.001, 0.1);
    }
    return newShadowPos;
  }
  vec4 ApplyShadowDepthBias_Perspective(vec4 shadowPos, float viewspaceDepthBias)
  {
    vec3 viewSpacePos;
    viewSpacePos.xy = shadowPos.xy * cc_shadowProjInfo.zw;
    viewSpacePos.z = shadowPos.z * cc_shadowInvProjDepthInfo.x + shadowPos.w * cc_shadowInvProjDepthInfo.y;
    viewSpacePos.xyz += cc_shadowProjDepthInfo.z * normalize(viewSpacePos.xyz) * viewspaceDepthBias;
    vec4 clipSpacePos;
    clipSpacePos.xy = viewSpacePos.xy * cc_shadowProjInfo.xy;
    clipSpacePos.zw = viewSpacePos.z * cc_shadowProjDepthInfo.xz + vec2(cc_shadowProjDepthInfo.y, 0.0);
    if (cc_shadowNFLSInfo.z > 0.000001) {
      clipSpacePos.z = CCGetLinearDepthFromViewSpace(viewSpacePos.xyz);
      clipSpacePos.z = (clipSpacePos.z * 2.0 - 1.0) * clipSpacePos.w;
    }
    return clipSpacePos;
  }
  vec4 ApplyShadowDepthBias_Orthographic(vec4 shadowPos, float viewspaceDepthBias)
  {
    float coeffA = cc_shadowProjDepthInfo.x;
    float coeffB = cc_shadowProjDepthInfo.y;
    float viewSpacePos_z = (shadowPos.z - coeffB) / coeffA;
    viewSpacePos_z += viewspaceDepthBias;
    vec4 result = shadowPos;
    result.z = viewSpacePos_z * coeffA + coeffB;
    return result;
  }
  float CCGetShadowFactorHard (vec4 shadowPos) {
    vec4 clipPosNew = ApplyShadowDepthBias_Orthographic(shadowPos, cc_shadowWHPBInfo.w);
    vec3 clipPos = clipPosNew.xyz / clipPosNew.w * 0.5 + 0.5;
    if (clipPos.x < 0.0 || clipPos.x > 1.0 ||
        clipPos.y < 0.0 || clipPos.y > 1.0 ||
        clipPos.z < 0.0 || clipPos.z > 1.0) { return 1.0; }
    clipPos.xy = cc_cameraPos.w == 1.0 ? vec2(clipPos.xy.x, 1.0 - clipPos.xy.y) : clipPos.xy;
    float shadow = 0.0;
    float closestDepth = 0.0;
    if (cc_shadowLPNNInfo.y > 0.000001) {
      closestDepth = dot(texture(cc_shadowMap, clipPos.xy), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0));
    } else {
      closestDepth = texture(cc_shadowMap, clipPos.xy).x;
    }
    shadow = step(clipPos.z, closestDepth);
    return shadow;
  }
  float CCGetShadowFactorSoft (vec4 shadowPos) {
    vec4 clipPosNew = ApplyShadowDepthBias_Orthographic(shadowPos, cc_shadowWHPBInfo.w);
    vec3 clipPos = clipPosNew.xyz / clipPosNew.w * 0.5 + 0.5;
    if (clipPos.x < 0.0 || clipPos.x > 1.0 ||
        clipPos.y < 0.0 || clipPos.y > 1.0 ||
        clipPos.z < 0.0 || clipPos.z > 1.0) { return 1.0; }
    clipPos.xy = cc_cameraPos.w == 1.0 ? vec2(clipPos.xy.x, 1.0 - clipPos.xy.y) : clipPos.xy;
    float offsetDepth = clipPos.z;
    vec2 mapSize = cc_shadowWHPBInfo.xy;
    vec2 oneTap = 1.0 / mapSize;
    vec2 clipPos_offset = clipPos.xy + oneTap;
    float block0, block1, block2, block3;
    if (cc_shadowLPNNInfo.y > 0.000001) {
      block0 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos.x, clipPos.y)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block1 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset.x, clipPos.y)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block2 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos.x, clipPos_offset.y)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block3 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset.x, clipPos_offset.y)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
    } else {
      block0 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos.x, clipPos.y)).x);
      block1 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset.x, clipPos.y)).x);
      block2 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos.x, clipPos_offset.y)).x);
      block3 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset.x, clipPos_offset.y)).x);
    }
    float coefX   = mod(clipPos.x, oneTap.x) * mapSize.x;
    float resultX = mix(block0, block1, coefX);
    float resultY = mix(block2, block3, coefX);
    float coefY   = mod(clipPos.y, oneTap.y) * mapSize.y;
    return mix(resultX, resultY, coefY);
  }
  float CCGetShadowFactorSoft2X (vec4 shadowPos) {
    vec4 clipPosNew = ApplyShadowDepthBias_Orthographic(shadowPos, cc_shadowWHPBInfo.w);
    vec3 clipPos = clipPosNew.xyz / clipPosNew.w * 0.5 + 0.5;
    if (clipPos.x < 0.0 || clipPos.x > 1.0 ||
        clipPos.y < 0.0 || clipPos.y > 1.0 ||
        clipPos.z < 0.0 || clipPos.z > 1.0) { return 1.0; }
    clipPos.xy = cc_cameraPos.w == 1.0 ? vec2(clipPos.xy.x, 1.0 - clipPos.xy.y) : clipPos.xy;
    float offsetDepth = clipPos.z;
    vec2 mapSize = cc_shadowWHPBInfo.xy;
    vec2 oneTap = 1.0 / mapSize;
    float clipPos_offset_L = clipPos.x - oneTap.x;
    float clipPos_offset_R = clipPos.x + oneTap.x;
    float clipPos_offset_U = clipPos.y - oneTap.y;
    float clipPos_offset_D = clipPos.y + oneTap.y;
    float block0, block1, block2, block3, block4, block5, block6, block7, block8;
    if (cc_shadowLPNNInfo.y > 0.000001) {
      block0 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset_L, clipPos_offset_U)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block1 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos.x, clipPos_offset_U)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block2 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset_R, clipPos_offset_U)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block3 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset_L, clipPos.y)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block4 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos.x, clipPos.y)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block5 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset_R, clipPos.y)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block6 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset_L, clipPos_offset_D)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block7 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos.x, clipPos_offset_D)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
      block8 = step(offsetDepth, dot(texture(cc_shadowMap, vec2(clipPos_offset_R, clipPos_offset_D)), vec4(1.0, 1.0 / 255.0, 1.0 / 65025.0, 1.0 / 16581375.0)));
    } else {
      block0 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset_L, clipPos_offset_U)).x);
      block1 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos.x, clipPos_offset_U)).x);
      block2 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset_R, clipPos_offset_U)).x);
      block3 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset_L, clipPos.y)).x);
      block4 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos.x, clipPos.y)).x);
      block5 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset_R, clipPos.y)).x);
      block6 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset_L, clipPos_offset_D)).x);
      block7 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos.x, clipPos_offset_D)).x);
      block8 = step(offsetDepth, texture(cc_shadowMap, vec2(clipPos_offset_R, clipPos_offset_D)).x);
    }
    float coefX = mod(clipPos.x, oneTap.x) * mapSize.x;
    float coefY = mod(clipPos.y, oneTap.y) * mapSize.y;
    float shadow = 0.0;
    float resultX = mix(block0, block1, coefX);
    float resultY = mix(block3, block4, coefX);
    shadow += mix(resultX , resultY, coefY);
    resultX = mix(block1, block2, coefX);
    resultY = mix(block4, block5, coefX);
    shadow += mix(resultX , resultY, coefY);
    resultX = mix(block3, block4, coefX);
    resultY = mix(block6, block7, coefX);
    shadow += mix(resultX, resultY, coefY);
    resultX = mix(block4, block5, coefX);
    resultY = mix(block7, block8, coefX);
    shadow += mix(resultX, resultY, coefY);
