precision mediump float;
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(std140) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
layout(std140) uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
  highp vec4 cc_lightingMapUVParam;
};
float LinearFog(vec4 pos) {
  vec4 wPos = pos;
  float cam_dis = distance(cc_cameraPos, wPos);
  float fogStart = cc_fogBase.x;
  float fogEnd = cc_fogBase.y;
  return clamp((fogEnd - cam_dis) / (fogEnd - fogStart), 0., 1.);
}
float ExpFog(vec4 pos) {
  vec4 wPos = pos;
  float fogAtten = cc_fogAdd.z;
  float fogStart = cc_fogBase.x;
  float fogDensity = cc_fogBase.z;
  float cam_dis = max(distance(cc_cameraPos, wPos) - fogStart, 0.0) / fogAtten * 4.;
  float f = exp(-cam_dis * fogDensity);
  return f;
}
float ExpSquaredFog(vec4 pos) {
  vec4 wPos = pos;
  float fogAtten = cc_fogAdd.z;
  float fogStart = cc_fogBase.x;
  float fogDensity = cc_fogBase.z;
  float cam_dis = max(distance(cc_cameraPos, wPos) - fogStart, 0.0) / fogAtten * 4.;
  float f = exp(-cam_dis * cam_dis * fogDensity * fogDensity);
  return f;
}
float LayeredFog(vec4 pos) {
  vec4 wPos = pos;
  float fogAtten = cc_fogAdd.z;
  float _FogTop = cc_fogAdd.x;
  float _FogRange = cc_fogAdd.y;
  vec3 camWorldProj = cc_cameraPos.xyz;
  camWorldProj.y = 0.;
  vec3 worldPosProj = wPos.xyz;
  worldPosProj.y = 0.;
  float fDeltaD = distance(worldPosProj, camWorldProj) / fogAtten * 2.0;
  float fDeltaY, fDensityIntegral;
  if (cc_cameraPos.y > _FogTop) {
    if (wPos.y < _FogTop) {
      fDeltaY = (_FogTop - wPos.y) / _FogRange * 2.0;
      fDensityIntegral = fDeltaY * fDeltaY * 0.5;
    } else {
      fDeltaY = 0.;
      fDensityIntegral = 0.;
    }
  } else {
    if (wPos.y < _FogTop) {
      float fDeltaA = (_FogTop - cc_cameraPos.y) / _FogRange * 2.;
      float fDeltaB = (_FogTop - wPos.y) / _FogRange * 2.;
      fDeltaY = abs(fDeltaA - fDeltaB);
      fDensityIntegral = abs((fDeltaA * fDeltaA * 0.5) - (fDeltaB * fDeltaB * 0.5));
    } else {
      fDeltaY = abs(_FogTop - cc_cameraPos.y) / _FogRange * 2.;
      fDensityIntegral = abs(fDeltaY * fDeltaY * 0.5);
    }
  }
  float fDensity;
  if (fDeltaY != 0.) {
    fDensity = (sqrt(1.0 + ((fDeltaD / fDeltaY) * (fDeltaD / fDeltaY)))) * fDensityIntegral;
  } else {
    fDensity = 0.;
  }
  float f = exp(-fDensity);
  return f;
}
void CC_TRANSFER_FOG_BASE(vec4 pos, out float factor)
{
  #if CC_USE_FOG == 0
	factor = LinearFog(pos);
  #elif CC_USE_FOG == 1
    factor = ExpFog(pos);
  #elif CC_USE_FOG == 2
    factor = ExpSquaredFog(pos);
  #elif CC_USE_FOG == 3
    factor = LayeredFog(pos);
  #else
    factor = 1.0;
  #endif
}
#if !CC_USE_ACCURATE_FOG
out float v_fog_factor;
#endif
void CC_TRANSFER_FOG(vec4 pos) {
#if !CC_USE_ACCURATE_FOG
    CC_TRANSFER_FOG_BASE(pos, v_fog_factor);
#endif
}
out highp vec4 v_shadowPos;
layout(std140) uniform CCShadow {
  highp mat4 cc_matLightPlaneProj;
  highp mat4 cc_matLightView;
  highp mat4 cc_matLightViewProj;
  highp vec4 cc_shadowInvProjDepthInfo;
  highp vec4 cc_shadowProjDepthInfo;
  highp vec4 cc_shadowProjInfo;
  lowp  vec4 cc_shadowNFLSInfo;
  lowp  vec4 cc_shadowWHPBInfo;
  lowp  vec4 cc_shadowLPNNInfo;
  lowp  vec4 cc_shadowColor;
};
#if CC_RECEIVE_SHADOW
  uniform highp sampler2D cc_shadowMap;
  uniform highp sampler2D cc_spotLightingMap;
#endif
in vec3 a_position;
in vec3 a_normal;
in vec2 a_texCoord;
out highp vec3 v_position;
out mediump vec3 v_normal;
#if USE_NORMALMAP
  out mediump vec3 v_tangent;
  out mediump vec3 v_binormal;
#endif
out mediump vec2 uvw;
out mediump vec2 uv0;
out mediump vec2 uv1;
out mediump vec2 uv2;
out mediump vec2 uv3;
out mediump vec3 luv;
out mediump vec3 diffuse;
layout(std140) uniform TexCoords {
  vec4 UVScale;
  vec4 lightMapUVParam;
};
void main () {
  vec3 worldPos;
  worldPos.x = cc_matWorld[3][0] + a_position.x;
  worldPos.y = cc_matWorld[3][1] + a_position.y;
  worldPos.z = cc_matWorld[3][2] + a_position.z;
  vec4 pos = vec4(worldPos, 1.0);
  pos = cc_matViewProj * pos;
  uvw = a_texCoord;
  uv0 = a_position.xz * UVScale.x;
  uv1 = a_position.xz * UVScale.y;
  uv2 = a_position.xz * UVScale.z;
  uv3 = a_position.xz * UVScale.w;
  #if USE_LIGHTMAP
    luv.xy = lightMapUVParam.xy + a_texCoord * lightMapUVParam.zw;
    luv.z = lightMapUVParam.z;
  #endif
  v_position = worldPos;
  v_normal = a_normal;
  CC_TRANSFER_FOG(vec4(worldPos, 1.0));
  #if USE_NORMALMAP
    v_tangent = vec3(1.0, 0.0, 0.0);
    v_binormal = vec3(0.0, 0.0, 1.0);
    v_binormal = cross(v_tangent, a_normal);
    v_tangent = cross(a_normal, v_binormal);
  #endif
  v_shadowPos = cc_matLightViewProj * vec4(worldPos, 1.0);
  gl_Position = pos;
}

