  worldPos.z = cc_matWorld[3][2] + a_position.z;
  worldPos.w = 1.0;
  vec4 clipPos = cc_matLightViewProj * worldPos;
  v_clip_depth = clipPos.zw;
  return clipPos;
}
void main() { gl_Position = vert(); }

