  vec4 fragTextureLod (sampler2D tex, vec2 coord, float lod) {
      #ifdef GL_EXT_shader_texture_lod
        return texture2DLodEXT(tex, coord, lod);
      #else
        return texture2D(tex, coord, lod);
      #endif
  }
  vec4 fragTextureLod (samplerCube tex, vec3 coord, float lod) {
      #ifdef GL_EXT_shader_texture_lod
        return textureCubeLodEXT(tex, coord, lod);
      #else
        return textureCube(tex, coord, lod);
      #endif
  }
  vec3 unpackRGBE (vec4 rgbe) {
    return rgbe.rgb * pow(1.1, rgbe.a * 255.0 - 128.0);
  }
  #if CC_USE_DIFFUSEMAP
    uniform samplerCube cc_diffuseMap;
  #endif
#endif
float GGXMobile (float roughness, float NoH, vec3 H, vec3 N) {
  vec3 NxH = cross(N, H);
  float OneMinusNoHSqr = dot(NxH, NxH);
  float a = roughness * roughness;
  float n = NoH * a;
  float p = a / (OneMinusNoHSqr + n * n);
  return p * p;
}
float CalcSpecular (float roughness, float NoH, vec3 H, vec3 N) {
  return (roughness * 0.25 + 0.25) * GGXMobile(roughness, NoH, H, N);
}
vec3 BRDFApprox (vec3 specular, float roughness, float NoV) {
  const vec4 c0 = vec4(-1.0, -0.0275, -0.572, 0.022);
  const vec4 c1 = vec4(1.0, 0.0425, 1.04, -0.04);
  vec4 r = roughness * c0 + c1;
  float a004 = min(r.x * r.x, exp2(-9.28 * NoV)) * r.x + r.y;
  vec2 AB = vec2(-1.04, 1.04) * a004 + r.zw;
  AB.y *= clamp(50.0 * specular.g, 0.0, 1.0);
  return specular * AB.x + AB.y;
}
#if USE_REFLECTION_DENOISE
  vec3 GetEnvReflectionWithMipFiltering(vec3 R, float roughness, float mipCount, float denoiseIntensity) {
    #if CC_USE_IBL
    	float mip = roughness * mipCount;
    	float delta = (dot(dFdx(R), dFdy(R))) * 1000.0;
    	float mipBias = mix(0.0, 5.0, clamp(delta, 0.0, 1.0));
    	vec4 biased = fragTextureLod(cc_environment, R, mip + mipBias);
     	vec4 filtered = textureCube(cc_environment, R);
      #if CC_USE_IBL == 2
      	biased.rgb = unpackRGBE(biased);
      	filtered.rgb = unpackRGBE(filtered);
      #else
      	biased.rgb = SRGBToLinear(biased.rgb);
      	filtered.rgb = SRGBToLinear(filtered.rgb);
      #endif
      return mix(biased.rgb, filtered.rgb, denoiseIntensity);
    #else
      return vec3(0.0, 0.0, 0.0);
    #endif
  }
#endif
struct StandardSurface {
  vec4 albedo;
  vec3 position;
  vec3 normal;
  vec3 emissive;
  vec3 lightmap;
  float lightmap_test;
  float roughness;
  float metallic;
  float occlusion;
};
vec4 CCStandardShadingBase (StandardSurface s, vec4 shadowPos) {
  vec3 diffuse = s.albedo.rgb * (1.0 - s.metallic);
  vec3 specular = mix(vec3(0.04), s.albedo.rgb, s.metallic);
  vec3 N = normalize(s.normal);
  vec3 V = normalize(cc_cameraPos.xyz - s.position);
  float NV = max(abs(dot(N, V)), 0.0);
  specular = BRDFApprox(specular, s.roughness, NV);
  vec3 L = normalize(-cc_mainLitDir.xyz);
  vec3 H = normalize(L + V);
  float NH = max(dot(N, H), 0.0);
  float NL = max(dot(N, L), 0.0);
  vec3 finalColor = NL * cc_mainLitColor.rgb * cc_mainLitColor.w;
  vec3 diffuseContrib = diffuse;
  #if USE_LIGHTMAP && !USE_BATCHING && !CC_FORWARD_ADD
    if (s.lightmap_test > 0.0001) {
      finalColor = s.lightmap.rgb;
    }
  #else
    diffuseContrib /= 3.14159265359;
  #endif
  vec3 specularContrib = specular * CalcSpecular(s.roughness, NH, H, N);
  vec3 dirlightContrib = (diffuseContrib + specularContrib);
  float shadow = 1.0;
  #if CC_RECEIVE_SHADOW && CC_ENABLE_DIR_SHADOW
    if (NL > 0.0) {
      {
        vec4 pos = ApplyShadowDepthBias_FaceNormal(shadowPos, N);
        float pcf = cc_shadowWHPBInfo.z;
        if (pcf > 1.9) shadow = CCGetShadowFactorSoft2X(pos);
        else if (pcf > 0.9) shadow = CCGetShadowFactorSoft(pos);
        else shadow = CCGetShadowFactorHard(pos);
        shadow = mix(shadow, 1.0, cc_shadowNFLSInfo.w);
      }
    }
  #endif
  dirlightContrib *= shadow;
  finalColor *= dirlightContrib;
  float fAmb = 0.5 - N.y * 0.5;
  vec3 ambDiff = mix(cc_ambientSky.rgb, cc_ambientGround.rgb, fAmb);
  #if CC_USE_IBL
    #if CC_USE_DIFFUSEMAP
      vec4 diffuseMap = textureCube(cc_diffuseMap, N);
      #if CC_USE_DIFFUSEMAP == 2
        ambDiff = unpackRGBE(diffuseMap);
      #else
        ambDiff = SRGBToLinear(diffuseMap.rgb);
      #endif
    #endif
    vec3 R = normalize(reflect(-V, N));
    #if USE_REFLECTION_DENOISE
      vec3 env = GetEnvReflectionWithMipFiltering(R, s.roughness, cc_ambientGround.w, 0.6);
    #else
      vec4 envmap = fragTextureLod(cc_environment, R, s.roughness * cc_ambientGround.w);
      #if CC_USE_IBL == 2
        vec3 env = unpackRGBE(envmap);
      #else
        vec3 env = SRGBToLinear(envmap.rgb);
      #endif
    #endif
    finalColor += env * cc_ambientSky.w * specular * s.occlusion;
  #endif
  finalColor += ambDiff.rgb * cc_ambientSky.w * diffuse * s.occlusion;
  finalColor += s.emissive;
  return vec4(finalColor, s.albedo.a);
}
vec3 ACESToneMap (vec3 color) {
  color = min(color, vec3(8.0));
  const float A = 2.51;
  const float B = 0.03;
  const float C = 2.43;
  const float D = 0.59;
  const float E = 0.14;
  return (color * (A * color + B)) / (color * (C * color + D) + E);
}
vec4 CCFragOutput (vec4 color) {
  #if CC_USE_HDR
    color.rgb = ACESToneMap(color.rgb);
  #endif
  color.rgb = sqrt(color.rgb);
  return color;
}
varying highp vec4 v_shadowPos;
#if USE_LIGHTMAP && !USE_BATCHING && !CC_FORWARD_ADD
  varying vec3 v_luv;
  uniform sampler2D cc_lightingMap;
#endif
varying vec3 v_position;
varying vec2 v_uv;
varying vec2 v_uv1;
varying vec3 v_normal;
#if USE_VERTEX_COLOR
  varying vec4 v_color;
#endif
#if USE_ALBEDO_MAP
  uniform sampler2D albedoMap;
#endif
#if USE_NORMAL_MAP
  varying vec3 v_tangent;
  varying vec3 v_bitangent;
  uniform sampler2D normalMap;
#endif
#if USE_PBR_MAP
  uniform sampler2D pbrMap;
#endif
#if USE_METALLIC_ROUGHNESS_MAP
  uniform sampler2D metallicRoughnessMap;
#endif
#if USE_OCCLUSION_MAP
  uniform sampler2D occlusionMap;
#endif
#if USE_EMISSIVE_MAP
  uniform sampler2D emissiveMap;
#endif
#if USE_ALPHA_TEST
#endif
void surf (out StandardSurface s) {
  vec4 baseColor = albedo;
  #if USE_VERTEX_COLOR
    baseColor *= v_color;
  #endif
  #if USE_ALBEDO_MAP
    vec4 texColor = texture2D(albedoMap, ALBEDO_UV);
    texColor.rgb = SRGBToLinear(texColor.rgb);
    baseColor *= texColor;
  #endif
  s.albedo = baseColor;
  s.albedo.rgb *= albedoScaleAndCutoff.xyz;
  #if USE_ALPHA_TEST
    if (s.albedo.ALPHA_TEST_CHANNEL < albedoScaleAndCutoff.w) discard;
  #endif
  #if USE_LIGHTMAP && !USE_BATCHING && !CC_FORWARD_ADD
    s.lightmap = texture2D(cc_lightingMap, v_luv);
  #endif
  s.normal = v_normal;
  #if USE_NORMAL_MAP
    vec3 nmmp = texture2D(normalMap, NORMAL_UV).xyz - vec3(0.5);
    s.normal =
      (nmmp.x * pbrParams.w) * normalize(v_tangent) +
      (nmmp.y * pbrParams.w) * normalize(v_bitangent) +
      nmmp.z * normalize(s.normal);
  #endif
  s.position = v_position;
  vec4 pbr = pbrParams;
  #if USE_PBR_MAP
    vec4 res = texture2D(pbrMap, PBR_UV);
    pbr.x *= res.r;
    pbr.y *= res.g;
    pbr.z *= res.b;
  #endif
  #if USE_METALLIC_ROUGHNESS_MAP
    vec4 metallicRoughness = texture2D(metallicRoughnessMap, PBR_UV);
    pbr.z *= metallicRoughness.b;
    pbr.y *= metallicRoughness.g;
  #endif
  #if USE_OCCLUSION_MAP
    pbr.x *= texture2D(occlusionMap, PBR_UV).r;
  #endif
  s.occlusion = pbr.x;
  s.roughness = pbr.y;
  s.metallic = pbr.z;
  s.emissive = emissive.rgb * emissiveScaleParam.xyz;
  #if USE_EMISSIVE_MAP
    s.emissive *= SRGBToLinear(texture2D(emissiveMap, EMISSIVE_UV).rgb);
  #endif
