uniform sampler2D cc_reflectionTexture;
void main () {
  StandardSurface s; surf(s);
  vec4 color = CCStandardShadingBase(s, v_shadowPos);
  CC_APPLY_FOG(color, s.position.xyz);
  vec4 baseColor = CCFragOutput(color);
  vec2 noise = v_normal.xy * 0.02;
  #if USE_NORMAL_MAP
    noise = texture2D(normalMap, ALBEDO_UV).xy * 0.02;
  #endif
  vec2 resolution = cc_screenSize.xy;
  vec2 uv = (gl_FragCoord.xy / resolution) + noise;
  vec4 reflectionColor = texture2D(cc_reflectionTexture, uv);
  gl_FragColor = mix(baseColor, reflectionColor, 0.5);
  gl_FragColor.w = 1.0;
}
