  CCLight getCCLight(uint i)
  {
    CCLight light;
    light.cc_lightPos = b_ccLights[4u * i + 0u];
    light.cc_lightColor = b_ccLights[4u * i + 1u];
    light.cc_lightSizeRangeAngle = b_ccLights[4u * i + 2u];
    light.cc_lightDir = b_ccLights[4u * i + 3u];
    return light;
  }
  LightGrid getLightGrid(uint cluster)
  {
    uvec4 gridvec = b_clusterLightGrid[cluster];
    LightGrid grid;
    grid.offset = gridvec.x;
    grid.ccLights = gridvec.y;
    return grid;
  }
  uint getGridLightIndex(uint start, uint offset)
  {
    return b_clusterLightIndices[start + offset];
  }
  uint getClusterZIndex(vec4 worldPos)
  {
    float scale = float(24) / log(cc_nearFar.y / cc_nearFar.x);
    float bias = -(float(24) * log(cc_nearFar.x) / log(cc_nearFar.y / cc_nearFar.x));
    float eyeDepth = -(cc_matView * worldPos).z;
    uint zIndex = uint(max(log(eyeDepth) * scale + bias, 0.0));
    return zIndex;
  }
  uint getClusterIndex(vec4 fragCoord, vec4 worldPos)
  {
    uint zIndex = getClusterZIndex(worldPos);
    float clusterSizeX = ceil(cc_screenSize.x / float(16));
    float clusterSizeY = ceil(cc_screenSize.y / float(8));
    uvec3 indices = uvec3(uvec2(fragCoord.xy / vec2(clusterSizeX, clusterSizeY)), zIndex);
    uint cluster = (16u * 8u) * indices.z + 16u * indices.y + indices.x;
    return cluster;
  }
  vec4 CCClusterShadingAdditive (StandardSurface s, vec4 shadowPos) {
    vec3 diffuse = s.albedo.rgb * (1.0 - s.metallic);
    vec3 specular = mix(vec3(0.04), s.albedo.rgb, s.metallic);
    vec3 diffuseContrib = diffuse / 3.14159265359;
    vec3 N = normalize(s.normal);
    vec3 V = normalize(cc_cameraPos.xyz - s.position);
    float NV = max(abs(dot(N, V)), 0.001);
    specular = BRDFApprox(specular, s.roughness, NV);
    vec3 finalColor = vec3(0.0);
    uint cluster = getClusterIndex(gl_FragCoord, vec4(s.position, 1.0));
    LightGrid grid = getLightGrid(cluster);
    uint numLights = grid.ccLights;
    for (uint i = 0u; i < 100u; i++) {
      if (i >= numLights) break;
      uint lightIndex = getGridLightIndex(grid.offset, i);
      CCLight light = getCCLight(lightIndex);
      vec3 SLU = light.cc_lightPos.xyz - s.position;
      vec3 SL = normalize(SLU);
      vec3 SH = normalize(SL + V);
      float SNL = max(dot(N, SL), 0.001);
      float SNH = max(dot(N, SH), 0.0);
      float distSqr = dot(SLU, SLU);
      float litRadius = light.cc_lightSizeRangeAngle.x;
      float litRadiusSqr = litRadius * litRadius;
      float illum = 3.14159265359 * (litRadiusSqr / max(litRadiusSqr , distSqr));
      float attRadiusSqrInv = 1.0 / max(light.cc_lightSizeRangeAngle.y, 0.01);
      attRadiusSqrInv *= attRadiusSqrInv;
      float att = GetDistAtt(distSqr, attRadiusSqrInv);
      vec3 lspec = specular * CalcSpecular(s.roughness, SNH, SH, N);
      if (light.cc_lightPos.w > 0.0) {
        float cosInner = max(dot(-light.cc_lightDir.xyz, SL), 0.01);
        float cosOuter = light.cc_lightSizeRangeAngle.z;
        float litAngleScale = 1.0 / max(0.001, cosInner - cosOuter);
        float litAngleOffset = -cosOuter * litAngleScale;
        att *= GetAngleAtt(SL, -light.cc_lightDir.xyz, litAngleScale, litAngleOffset);
      }
      vec3 lightColor = light.cc_lightColor.rgb;
      float shadow = 1.0;
      #if CC_RECEIVE_SHADOW
        if (light.cc_lightPos.w > 0.0) {
            {
              float pcf = cc_shadowWHPBInfo.z;
              if (pcf > 1.9) shadow = CCGetSpotLightShadowFactorSoft2X(shadowPos, s.position);
              else if (pcf > 0.9) shadow = CCGetSpotLightShadowFactorSoft(shadowPos, s.position);
              else shadow = CCGetSpotLightShadowFactorHard(shadowPos, s.position);
            }
        }
      #endif
      lightColor *= shadow;
      finalColor += SNL * lightColor * light.cc_lightColor.w * illum * att * (diffuseContrib + lspec);
    }
    return vec4(finalColor, 0.0);
  }
  #endif
  vec3 ACESToneMap (vec3 color) {
    color = min(color, vec3(8.0));
    const float A = 2.51;
    const float B = 0.03;
    const float C = 2.43;
    const float D = 0.59;
    const float E = 0.14;
    return (color * (A * color + B)) / (color * (C * color + D) + E);
  }
  vec4 CCFragOutput (vec4 color) {
    #if CC_USE_HDR
      color.rgb = ACESToneMap(color.rgb);
    #endif
    color.rgb = sqrt(color.rgb);
    return color;
  }
  float LinearFog(vec4 pos) {
    vec4 wPos = pos;
    float cam_dis = distance(cc_cameraPos, wPos);
    float fogStart = cc_fogBase.x;
    float fogEnd = cc_fogBase.y;
    return clamp((fogEnd - cam_dis) / (fogEnd - fogStart), 0., 1.);
  }
  float ExpFog(vec4 pos) {
    vec4 wPos = pos;
    float fogAtten = cc_fogAdd.z;
    float fogStart = cc_fogBase.x;
    float fogDensity = cc_fogBase.z;
    float cam_dis = max(distance(cc_cameraPos, wPos) - fogStart, 0.0) / fogAtten * 4.;
    float f = exp(-cam_dis * fogDensity);
    return f;
  }
  float ExpSquaredFog(vec4 pos) {
    vec4 wPos = pos;
    float fogAtten = cc_fogAdd.z;
    float fogStart = cc_fogBase.x;
    float fogDensity = cc_fogBase.z;
    float cam_dis = max(distance(cc_cameraPos, wPos) - fogStart, 0.0) / fogAtten * 4.;
    float f = exp(-cam_dis * cam_dis * fogDensity * fogDensity);
    return f;
  }
  float LayeredFog(vec4 pos) {
    vec4 wPos = pos;
    float fogAtten = cc_fogAdd.z;
    float _FogTop = cc_fogAdd.x;
    float _FogRange = cc_fogAdd.y;
    vec3 camWorldProj = cc_cameraPos.xyz;
    camWorldProj.y = 0.;
    vec3 worldPosProj = wPos.xyz;
    worldPosProj.y = 0.;
    float fDeltaD = distance(worldPosProj, camWorldProj) / fogAtten * 2.0;
    float fDeltaY, fDensityIntegral;
    if (cc_cameraPos.y > _FogTop) {
      if (wPos.y < _FogTop) {
        fDeltaY = (_FogTop - wPos.y) / _FogRange * 2.0;
        fDensityIntegral = fDeltaY * fDeltaY * 0.5;
      } else {
        fDeltaY = 0.;
        fDensityIntegral = 0.;
      }
    } else {
      if (wPos.y < _FogTop) {
        float fDeltaA = (_FogTop - cc_cameraPos.y) / _FogRange * 2.;
        float fDeltaB = (_FogTop - wPos.y) / _FogRange * 2.;
        fDeltaY = abs(fDeltaA - fDeltaB);
        fDensityIntegral = abs((fDeltaA * fDeltaA * 0.5) - (fDeltaB * fDeltaB * 0.5));
      } else {
        fDeltaY = abs(_FogTop - cc_cameraPos.y) / _FogRange * 2.;
        fDensityIntegral = abs(fDeltaY * fDeltaY * 0.5);
      }
    }
    float fDensity;
    if (fDeltaY != 0.) {
      fDensity = (sqrt(1.0 + ((fDeltaD / fDeltaY) * (fDeltaD / fDeltaY)))) * fDensityIntegral;
    } else {
      fDensity = 0.;
    }
    float f = exp(-fDensity);
    return f;
  }
  void CC_TRANSFER_FOG_BASE(vec4 pos, out float factor)
  {
    #if CC_USE_FOG == 0
  	factor = LinearFog(pos);
    #elif CC_USE_FOG == 1
      factor = ExpFog(pos);
    #elif CC_USE_FOG == 2
      factor = ExpSquaredFog(pos);
    #elif CC_USE_FOG == 3
      factor = LayeredFog(pos);
    #else
      factor = 1.0;
    #endif
  }
  void CC_APPLY_FOG_BASE(inout vec4 color, float factor) {
    color = vec4(mix(cc_fogColor.rgb, color.rgb, factor), color.a);
  }
  varying vec2 v_uv;
  #if CC_DEVICE_CAN_BENEFIT_FROM_INPUT_ATTACHMENT
  #else
    uniform sampler2D gbuffer_albedoMap;
    uniform sampler2D gbuffer_positionMap;
    uniform sampler2D gbuffer_normalMap;
    uniform sampler2D gbuffer_emissiveMap;
  #endif
  #if !CC_DEVICE_CAN_BENEFIT_FROM_INPUT_ATTACHMENT || __VERSION__ >= 450
  #endif
  void main () {
    StandardSurface s;
    #if CC_DEVICE_CAN_BENEFIT_FROM_INPUT_ATTACHMENT
        vec4 albedoMap = gl_LastFragData[0];
        vec4 positionMap = gl_LastFragData[1];
        vec4 normalMap = gl_LastFragData[2];
        vec4 emissiveMap = gl_LastFragData[3];
    #else
      vec4 albedoMap = texture2D(gbuffer_albedoMap,v_uv);
      vec4 positionMap = texture2D(gbuffer_positionMap,v_uv);
      vec4 normalMap = texture2D(gbuffer_normalMap,v_uv);
      vec4 emissiveMap = texture2D(gbuffer_emissiveMap,v_uv);
    #endif
    s.albedo = albedoMap;
    s.position = positionMap.xyz;
    s.roughness = positionMap.w;
    s.normal = normalMap.xyz;
    s.metallic = normalMap.w;
    s.emissive = emissiveMap.xyz;
    s.occlusion = emissiveMap.w;
    float fogFactor;
    CC_TRANSFER_FOG_BASE(vec4(s.position, 1), fogFactor);
    vec4 shadowPos;
      shadowPos = cc_matLightViewProj * vec4(s.position, 1);
    vec4 color = CCStandardShadingBase(s, shadowPos) +
#if CC_ENABLE_CLUSTERED_LIGHT_CULLING == 1
                 CCClusterShadingAdditive(s, shadowPos);
#else
                 CCStandardShadingAdditive(s, shadowPos);
#endif
    CC_APPLY_FOG_BASE(color, fogFactor);
    color = CCFragOutput(color);
    #if CC_DEVICE_CAN_BENEFIT_FROM_INPUT_ATTACHMENT
        gl_FragData[3] = color;
    #else
      gl_FragColor = color;
    #endif
  }
