uniform highp mat4 cc_matWorld;
uniform highp mat4 cc_matView;
  uniform highp mat4 cc_matProj;
attribute highp vec3 a_position;
attribute vec3 a_normal;
varying vec3 normal;
#if USE_DASHED_LINE
  attribute float a_lineDistance;
  varying float lineDistance;
#endif
vec4 vert () {
  normal = a_normal;
  vec4 pos = cc_matProj * (cc_matView * cc_matWorld) * vec4(a_position, 1);
  pos.z -= 0.000001;
  #if USE_DASHED_LINE
    lineDistance = a_lineDistance;
  #endif
  return pos;
}
void main() { gl_Position = vert(); }
