  v_uv = a_color.xy;
  if (a_color.z >= 0.0) {
    float n = getComponent(digits[int(a_color.z)], a_color.w);
    v_uv += vec2(offset.z * n, 0.0);
  }
  return vec4(position, 0.0, 1.0);
}
void main() { gl_Position = vert(); }
