  else if (config == 13) {
    n = 5;
    L[4] = L[3];
    L[3] = L[2];
    L[2] = -L[1].z * L[2] + L[2].z * L[1];
    L[1] = -L[1].z * L[0] + L[0].z * L[1];
  }
  else if (config == 14) {
    n = 5;
    L[4] = -L[0].z * L[3] + L[3].z * L[0];
