    if (Distance <= Radius) {
      k = 1.0;
    }
    else if (Distance > Radius + Falloff) {
      k = 0.0;
    }
    else {
      float y = (Distance - Radius) / Falloff;
      k = sqrt(1.0 - y * y);
    }
  #elif SPHERICAL
    if (Distance <= Radius) {
      k = 1.0;
    }
    else if (Distance > Radius + Falloff) {
      k = 0.0;
    }
    else {
      k = max(0.0, 1.0 - (Distance - Radius) / Falloff);
    }
    k = k*k*(3.0 - 2.0 * k);
  #elif TIP
    if (Distance <= Radius) {
      k = 1.0;
    }
    else if (Distance > Radius + Falloff) {
      k = 0.0;
    }
    else {
      float y = (Falloff + Radius - Distance) / Falloff;
      k = 1.0 - sqrt(1.0 - y * y);
    }
  #endif
